import { getByTitle } from "@testing-library/react";
import axios from "axios";
import React from "react";
import Card from "../Card/Card";
import DivideNumberIntoСategory from "../DivideNumberIntoСategory";
import styles from "./Order.module.scss";


const Order = ({ item}) => {
const [array,setArray]=React.useState([]);

React.useEffect(()=>{

  async function fetchData(){
    await item.map(item=> {
    axios.get(`http://localhost:5555/games/${item}`).then(resp=> array.push(resp.data))
    });

  }
  fetchData();
},[]);

React.useEffect(() =>{
  (async () => {
      const {data} = await axios.get('https://634e5d25f34e1ed826899d31.mockapi.io/orders');
      //setOrders(data.map((obj)=>obj.items).flat());
      //setOrders(data.map(obj=>obj.items));
      setTotalPrice(data.map(obj=>obj.totalPrice));
      setOrderId(data.map(obj => obj.id));
      setOrders(data.map(obj => obj.items));
      //console.log(data.map(obj => obj.items.map(obj1=> obj1.map(item => item.orderId)) = obj.id));
     setIsLoading(false);
     // console.log(orders);
  })();
},[])

  return( <div>
    <h2>Заказ # </h2>
    <div className={styles.order}>

      {array.map((obj) => (
       <Card
      key={obj.id}
       title={obj.title}
       imgURL={obj.imgURL}
       description={obj.description}
       price={obj.price}
     ></Card>
      ))}
    </div>
    <h4>
      Общая стоимость заказа (с учётом доставки и скидки):{" "}
      руб.
    </h4>
  </div>)
};

export default Order;
