import Card from "../components/Card/Card";

import React from "react";
import axios from "axios";
import Skeleton from "../components/Skeleton";


function Orders(){
    const [isLoading, setIsLoading] = React.useState(true);
    const [orders, setOrders] = React.useState([]);
  React.useEffect(() =>{
    (async () => {
        const {data} = await axios.get('https://634e5d25f34e1ed826899d31.mockapi.io/orders');
        //setOrders(data.map((obj)=>obj.items).flat());
        //setOrders(data.map(obj=>obj.items));
        setOrders(data.map(obj => obj.items).flatMap(item=>item));
        //console.log(data.map(obj => obj.items.map(obj1=> obj1.map(item => item.orderId)) = obj.id));
        setIsLoading(false);
    })();
  },[])

 return (
    <div className="content">
        <div className="titleAndSearch">
             <h1>Мои заказы</h1>
        </div>
    <div className="cards">


  {isLoading ? [...new Array(10)].map(()=><Skeleton/>) : orders.map((item,index) => 
    <Card>
    key = {item.title}
    id = {item.id}
    hash = {item.hash}
    title={item.title}
    imgURL={item.imgURL}
    description={item.description}
    price={item.price}


  </Card>)}

    </div>
  </div>
 );   
}
export default Orders;
