import axios from "axios";
import React from "react";
import Card from "../Card/Card";
import DivideNumberIntoСategory from "../DivideNumberIntoСategory";
import styles from "./Order.module.scss";


const Order = ({ item}) => {
const array = [];
React.useEffect(()=>{

  async function fetchData(){
    const token = window.localStorage.getItem('token');
    if (token){
      await axios('http://localhost:5555/cart',{
      headers: {
        'authorization': `Bearer ${token}`
      },
    }).then(resp=>resp.data.map(item=>axios.get(`http://localhost:5555/games/${item}`).then(res=>{
     cart.push(res.data)
    })));
    await axios('http://localhost:5555/favorites',{
      headers: {
        'authorization': `Bearer ${token}`
      },
    }).then(resp=>resp.data.map(item=>axios.get(`http://localhost:5555/games/${item}`).then(res=>{
      favorite.push(res.data)
    })));


    }

    const itemsResponse = await axios.get('http://localhost:5555/games');



    setCartItems(cart);
    setFavorites(favorite);
    setItems(itemsResponse.data);
    setIsLoading(false);

  }
  fetchData();
},[]);

  item.map(item=> axios.get(`http://localhost:5555/games/${item}`).then(resp=> array.push(resp.data)));

  return( <div>
    <h2>Заказ # </h2>
    <div className={styles.order}>
      {array.map((obj) => (
        <Card
          title={obj.title}
          imgURL={obj.imgURL}
          description={obj.description}
          price={obj.price}
        ></Card>
      ))}
    </div>
    <h4>
      Общая стоимость заказа (с учётом доставки и скидки):{" "}
      руб.
    </h4>
  </div>)
};

export default Order;
