function App() {
  const [cartOpened, setCartOpened]=React.useState(false);
  return (
    <div className="wrapper">
      {cartOpened ? <Drawer onClose = {()=>{
        setCartOpened(false);
      }}/> : undefined}
      <Header onClickCart = {()=>{
        setCartOpened(true);
      }}></Header>
      <div className="content">
        <div className="titleAndSearch">
          <h1>Все настолки</h1>
