import React from "react";
import Card from "../Card/Card";
import DivideNumberIntoСategory from "../DivideNumberIntoСategory";
import styles from "./Order.module.scss";


const Order = ({ item}) => {
const array = []
  item.map(item=> axios.get(`http://localhost:5555/games/${item}`).then(resp=> array.push(resp.data)));
  console.log(array);
  return( <div>
    {console.log}
    <h2>Заказ # </h2>
    <div className={styles.order}>
      {item.map((obj) => (
        <Card
          key={obj.title}
          id={obj._id}
          hash={563226265}
          title={obj.title}
          imgURL={obj.imgURL}
          description={obj.description}
          price={obj.price}
        ></Card>
      ))}
    </div>
    <h4>
      Общая стоимость заказа (с учётом доставки и скидки):{" "}
      руб.
    </h4>
  </div>)
};

export default Order;
