import React from "react";
import axios from "axios";
import Card from "../Card/Card";
import DivideNumberIntoСategory from "../DivideNumberIntoСategory";
import styles from "./Order.module.scss";

const Order = ({ item, orderId, totalPrice }) => {
  const arr=[];
  item.map(obj=>axios.get(`http://localhost:5555/games/${obj}`).then(resp=>{
  arr.push(resp.data.game);
  }))

 return (
    <div>
      <h2>Заказ # </h2>
      <div className={styles.order}>
        {arr.map((obj) => (
          <Card
            key={obj.title}
            id={obj.id}
            hash={obj.hash}
            title={obj.title}
            imgURL={obj.imgURL}
            description={obj.description}
            price={obj.price}
          ></Card>
        ))}

      </div>
      <h4>
        Общая стоимость заказа (с учётом доставки и скидки):{" "}
        <DivideNumberIntoСategory></DivideNumberIntoСategory> руб.
      </h4>
    </div>
  );
};

export default Order;
