    await item.map(item=> {
      axios.get(`http://localhost:5555/games/${item}`).then(function(item){
        return( <div>
          <h2>Заказ # </h2>
          <div className={styles.order}>

            {array.map((obj) => (
             <Card
            key={obj.id}
             title={obj.title}
             imgURL={obj.imgURL}
             description={obj.description}
             price={obj.price}
           ></Card>
            ))}
          </div>
          <h4>
            Общая стоимость заказа (с учётом доставки и скидки):{" "}
            руб.
          </h4>
        </div>)
      })
      })

  })();
},[item])


};

export default Order;
