import Card from "../components/Card/Card";
import AppContext from "../context";
import React from "react";
import style from '../index.module.scss'
import Skeleton from "../components/Skeleton";
import axios from "axios";
import styles from "./Favorites.module.scss";



function Favorites(){

  const {setIsLoading, isLoading} = React.useContext(AppContext);
  const [favorites, setFavorites] =  React.useState([]);
  const favorite = [];


  const removeFromFavorites = (id) => {
    const token = window.localStorage.getItem('token');
    if (token){
      axios(`http://localhost:5555/removeFromFavorites?productId=${id}`,{
      headers: {
        'authorization': `Bearer ${token}`
      },
    }).then(res=>setFavorites((prev)=> prev.filter(item => item._id !== id)));
  }


  }

  const onAddToFavorite = (id) => {
    let data = favorites.find(item=>item._id===id)
   if(!data){
     const newFavorite = []
   const token = window.localStorage.getItem('token');
   if (token){
     axios(`http://localhost:5555/addToFavorites?productId=${id}`,{
     headers: {
       'authorization': `Bearer ${token}`
     },
   }).then(resp=>resp.data.map(item=>axios.get(`http://localhost:5555/games/${item.id}`).then(res=>{
     newFavorite.push(res.data);
    })))
    setFavorites(newFavorite);
 }
   }
   else {
     removeFromFavorites(id);
   }
       //axios.post('https://634e5d25f34e1ed826899d31.mockapi.io/cart', obj).then(res=>setCartItems(prev=>[...prev, res.data]));


}
  React.useEffect(()=>{

    async function fetchData(){

      const token = window.localStorage.getItem('token');
      if (token && token !== null){
        await axios('http://localhost:5555/favorites',{
          headers: {
            'authorization': `Bearer ${token}`
          },
        }).then(resp=>resp.data.map(item=>axios.get(`http://localhost:5555/games/${item}`).then(res=>{
          favorite.push(res.data)
        }))).then(res=>setFavorites(favorite));
      }

      setIsLoading(false);

    }
    fetchData();
  },[]);
 return (
    <div className={style.content}>
        <div className={style.titleAndSearch}>
             <h1>Избранное</h1>
        </div>
    <div className={style.cards}>
      {isLoading ? [...new Array(10)].map(()=><Skeleton/>) : (favorites.length > 0 ? (favorites.map((item,index) => (
            <Card
              key = {item.title}
              id = {item.id}
              hash = {item.hash}
              title={item.title}
              imgURL={item.imgURL}
              description={item.description}
              price={item.price}
              favorited = {true}
              onFavorite = {() => onAddToFavorite(item._id)}
            ></Card>
    ))) : <div className= {styles.emptyBookmarks}>
    <img src = "/img/Broken_Heart_symbol.svg.png" width={130} height={130}></img>
    <h3>Вы не сдлелали сердечко красным ни на одном товаре :С</h3>
    <p>Пожалуйста, добавьте хотя бы один товар в избранное, чтобы отобразить его здесь.</p>
    </div> )}
    </div>
  </div>
 );   
}
export default Favorites;
