import React from 'react'
import AppContext from '../context';
import styles from './Drawer/Drawer.module.scss';



 const Info = ({title, imgURL, description}) => {
    const {setCartOpened} = React.useContext(AppContext);
  return (
    <div className={styles.emptyCart}>

            <img width = {150} height = {150} src='/img/empty-cart.jpg'></img>
            <h3>{title}</h3>
            <p>{description}</p>
            <button onClick={setCartOpened} className={styles.orangeButton}>
            <img className = {styles.arrow} src={imgURL} alt="arrow"></img>Вернуться назад
            </button>
            </div>
  )
}

export default Info;
