import styles from "./Card.module.scss";
import throttle from 'lodash.throttle';
import React from "react";
import AppContext from "../../context";
import DivideNumberIntoСategory from "../DivideNumberIntoСategory";

function Card({
  hash,
  onFavorite,
  imgURL,
  title,
  _id,
  price,
  description,
  onPlus,
  favorited = false,
  added = false
}) {

  const [isFavorite, setIsFavorite] = React.useState(favorited);
  const {isItemAdded,isAuth,addToCart} = React.useContext(AppContext);
  const onClick = throttle(addToCart, 50000);
  const [isCart, setIsCart] = React.useState(added);

  return (
    <div className={styles.card}>

          <div className={styles.cardTop}>
            {onFavorite && <div
              className={styles.favorite}
              onClick={() => {

                onFavorite(_id);
                setIsFavorite(!isFavorite);
              }}>
              {isAuth}
            </div>}
            <img
              width={133}
              height={112}
              src={imgURL}
              alt={"Изображение игры " + title}
            ></img>
            <h5>{title}</h5>
            <span>{description}</span>
          </div>
          <div className={styles.cardBottom}>
            <div className={styles.price}>
              <span> Цена:</span>
              <b><DivideNumberIntoСategory num = {price}></DivideNumberIntoСategory> руб.</b>
            </div>
            {onPlus && <img
              alt="Кнопка добавить или кнопка добавленно"
              className={styles.plus}
              onClick={()=>{
                onClick(_id);
                setIsCart(!isCart);
              }}
              width="15"
              height="15"
              src={isCart ? "/img/btn-checked.svg" : "/img/plus.svg"}
            ></img>}
          </div>

    </div>
  );
}
export default Card;
